package saturdayClass;

public class Counting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for(i =1; i<=100; i++) {
			System.out.println("this is the value of i:" + i);
		}
		
		for(i = 0; i<=100; i+=5) {
			System.out.println("this is the value of i:" + i);
			
		}

	}

}
